CREATE TABLE [Application].[Countries](	  [CountryID] INT NOT NULL DEFAULT(NEXT VALUE FOR [Sequences].[CountryID])	, [CountryName] NVARCHAR(60) COLLATE Latin1_General_CI_AS NOT NULL	, [FormalName] NVARCHAR(60) COLLATE Latin1_General_CI_AS NOT NULL	, [IsoAlpha3Code] NVARCHAR(3) COLLATE Latin1_General_CI_AS NULL	, [IsoNumericCode] INT NULL	, [CountryType] NVARCHAR(20) COLLATE Latin1_General_CI_AS NULL	, [LatestRecordedPopulation] BIGINT NULL	, [Continent] NVARCHAR(30) COLLATE Latin1_General_CI_AS NOT NULL	, [Region] NVARCHAR(30) COLLATE Latin1_General_CI_AS NOT NULL	, [Subregion] NVARCHAR(30) COLLATE Latin1_General_CI_AS NOT NULL	, [Border] GEOGRAPHY NULL	, [LastEditedBy] INT NOT NULL	, [ValidFrom] DATETIME2(7) NOT NULL	, [ValidTo] DATETIME2(7) NOT NULL	, CONSTRAINT [PK_Application_Countries] PRIMARY KEY ([CountryID] ASC))GOALTER TABLE [Application].[Countries] WITH CHECK ADD CONSTRAINT [FK_Application_Countries_Application_People] FOREIGN KEY([LastEditedBy]) REFERENCES [Application].[People] ([PersonID])GOALTER TABLE [Application].[Countries] CHECK CONSTRAINT [FK_Application_Countries_Application_People]GOCREATE UNIQUE NONCLUSTERED INDEX [UQ_Application_Countries_FormalName] ON [Application].[Countries] ([FormalName] ASC)GOCREATE UNIQUE NONCLUSTERED INDEX [UQ_Application_Countries_CountryName] ON [Application].[Countries] ([CountryName] ASC)GO