CREATE TABLE [Application].[People](	  [PersonID] INT NOT NULL DEFAULT(NEXT VALUE FOR [Sequences].[PersonID])	, [FullName] NVARCHAR(50) COLLATE Latin1_General_CI_AS NOT NULL	, [PreferredName] NVARCHAR(50) COLLATE Latin1_General_CI_AS NOT NULL	, [SearchName] AS (concat([PreferredName],N' ',[FullName]))	, [IsPermittedToLogon] BIT NOT NULL	, [LogonName] NVARCHAR(50) COLLATE Latin1_General_CI_AS NULL	, [IsExternalLogonProvider] BIT NOT NULL	, [HashedPassword] VARBINARY(MAX) NULL	, [IsSystemUser] BIT NOT NULL	, [IsEmployee] BIT NOT NULL	, [IsSalesperson] BIT NOT NULL	, [UserPreferences] NVARCHAR(MAX) COLLATE Latin1_General_CI_AS NULL	, [PhoneNumber] NVARCHAR(20) COLLATE Latin1_General_CI_AS NULL	, [FaxNumber] NVARCHAR(20) COLLATE Latin1_General_CI_AS NULL	, [EmailAddress] NVARCHAR(256) COLLATE Latin1_General_CI_AS NULL	, [Photo] VARBINARY(MAX) NULL	, [CustomFields] NVARCHAR(MAX) COLLATE Latin1_General_CI_AS NULL	, [OtherLanguages] AS (json_query([CustomFields],N'$.OtherLanguages'))	, [LastEditedBy] INT NOT NULL	, [ValidFrom] DATETIME2(7) NOT NULL	, [ValidTo] DATETIME2(7) NOT NULL	, CONSTRAINT [PK_Application_People] PRIMARY KEY ([PersonID] ASC))GOALTER TABLE [Application].[People] WITH CHECK ADD CONSTRAINT [FK_Application_People_Application_People] FOREIGN KEY([LastEditedBy]) REFERENCES [Application].[People] ([PersonID])GOALTER TABLE [Application].[People] CHECK CONSTRAINT [FK_Application_People_Application_People]GOCREATE NONCLUSTERED INDEX [IX_Application_People_FullName] ON [Application].[People] ([FullName] ASC)GOCREATE NONCLUSTERED INDEX [IX_Application_People_IsEmployee] ON [Application].[People] ([IsEmployee] ASC)GOCREATE NONCLUSTERED INDEX [IX_Application_People_IsSalesperson] ON [Application].[People] ([IsSalesperson] ASC)GOCREATE NONCLUSTERED INDEX [IX_Application_People_Perf_20160301_05] ON [Application].[People] ([IsPermittedToLogon] ASC, [PersonID] ASC)INCLUDE ([FullName], [EmailAddress])GO