CREATE TABLE [HumanResources].[Department](	  [DepartmentID] SMALLINT NOT NULL IDENTITY(1         ,1)	, [Name] NAME COLLATE Latin1_General_CI_AS NOT NULL	, [GroupName] NAME COLLATE Latin1_General_CI_AS NOT NULL	, [ModifiedDate] DATETIME NOT NULL DEFAULT(getdate())	, CONSTRAINT [PK_Department_DepartmentID] PRIMARY KEY ([DepartmentID] ASC))GOCREATE UNIQUE NONCLUSTERED INDEX [AK_Department_Name] ON [HumanResources].[Department] ([Name] ASC)GO