CREATE TABLE [HumanResources].[Employee](	  [BusinessEntityID] INT NOT NULL	, [NationalIDNumber] NVARCHAR(15) COLLATE Latin1_General_CI_AS NOT NULL	, [LoginID] NVARCHAR(256) COLLATE Latin1_General_CI_AS NOT NULL	, [OrganizationNode] HIERARCHYID NULL	, [OrganizationLevel] AS ([OrganizationNode].[GetLevel]())	, [JobTitle] NVARCHAR(50) COLLATE Latin1_General_CI_AS NOT NULL	, [BirthDate] DATE NOT NULL	, [MaritalStatus] NCHAR(1) COLLATE Latin1_General_CI_AS NOT NULL	, [Gender] NCHAR(1) COLLATE Latin1_General_CI_AS NOT NULL	, [HireDate] DATE NOT NULL	, [SalariedFlag] FLAG NOT NULL DEFAULT((1))	, [VacationHours] SMALLINT NOT NULL DEFAULT((0))	, [SickLeaveHours] SMALLINT NOT NULL DEFAULT((0))	, [CurrentFlag] FLAG NOT NULL DEFAULT((1))	, [rowguid] UNIQUEIDENTIFIER NOT NULL DEFAULT(newid())	, [ModifiedDate] DATETIME NOT NULL DEFAULT(getdate())	, CONSTRAINT [PK_Employee_BusinessEntityID] PRIMARY KEY ([BusinessEntityID] ASC))GOALTER TABLE [HumanResources].[Employee] WITH CHECK ADD CONSTRAINT [FK_Employee_Person_BusinessEntityID] FOREIGN KEY([BusinessEntityID]) REFERENCES [Person].[Person] ([BusinessEntityID])GOALTER TABLE [HumanResources].[Employee] CHECK CONSTRAINT [FK_Employee_Person_BusinessEntityID]GOCREATE UNIQUE NONCLUSTERED INDEX [AK_Employee_LoginID] ON [HumanResources].[Employee] ([LoginID] ASC)GOCREATE UNIQUE NONCLUSTERED INDEX [AK_Employee_NationalIDNumber] ON [HumanResources].[Employee] ([NationalIDNumber] ASC)GOCREATE UNIQUE NONCLUSTERED INDEX [AK_Employee_rowguid] ON [HumanResources].[Employee] ([rowguid] ASC)GOCREATE NONCLUSTERED INDEX [IX_Employee_OrganizationLevel_OrganizationNode] ON [HumanResources].[Employee] ([OrganizationLevel] ASC, [OrganizationNode] ASC)GOCREATE NONCLUSTERED INDEX [IX_Employee_OrganizationNode] ON [HumanResources].[Employee] ([OrganizationNode] ASC)GO