CREATE TABLE [Person].[Person](	  [BusinessEntityID] INT NOT NULL	, [PersonType] NCHAR(2) COLLATE Latin1_General_CI_AS NOT NULL	, [NameStyle] NAMESTYLE NOT NULL DEFAULT((0))	, [Title] NVARCHAR(8) COLLATE Latin1_General_CI_AS NULL	, [FirstName] NAME COLLATE Latin1_General_CI_AS NOT NULL	, [MiddleName] NAME COLLATE Latin1_General_CI_AS NULL	, [LastName] NAME COLLATE Latin1_General_CI_AS NOT NULL	, [Suffix] NVARCHAR(10) COLLATE Latin1_General_CI_AS NULL	, [EmailPromotion] INT NOT NULL DEFAULT((0))	, [AdditionalContactInfo] XML NULL	, [Demographics] XML NULL	, [rowguid] UNIQUEIDENTIFIER NOT NULL DEFAULT(newid())	, [ModifiedDate] DATETIME NOT NULL DEFAULT(getdate())	, CONSTRAINT [PK_Person_BusinessEntityID] PRIMARY KEY ([BusinessEntityID] ASC))GOALTER TABLE [Person].[Person] WITH CHECK ADD CONSTRAINT [FK_Person_BusinessEntity_BusinessEntityID] FOREIGN KEY([BusinessEntityID]) REFERENCES [Person].[BusinessEntity] ([BusinessEntityID])GOALTER TABLE [Person].[Person] CHECK CONSTRAINT [FK_Person_BusinessEntity_BusinessEntityID]GOCREATE UNIQUE NONCLUSTERED INDEX [AK_Person_rowguid] ON [Person].[Person] ([rowguid] ASC)GOCREATE NONCLUSTERED INDEX [IX_Person_LastName_FirstName_MiddleName] ON [Person].[Person] ([LastName] ASC, [FirstName] ASC, [MiddleName] ASC)GO