CREATE TABLE [Production].[ProductReview](	  [ProductReviewID] INT NOT NULL IDENTITY(1         ,1)	, [ProductID] INT NOT NULL	, [ReviewerName] NAME COLLATE Latin1_General_CI_AS NOT NULL	, [ReviewDate] DATETIME NOT NULL DEFAULT(getdate())	, [EmailAddress] NVARCHAR(50) COLLATE Latin1_General_CI_AS NOT NULL	, [Rating] INT NOT NULL	, [Comments] NVARCHAR(3850) COLLATE Latin1_General_CI_AS NULL	, [ModifiedDate] DATETIME NOT NULL DEFAULT(getdate())	, CONSTRAINT [PK_ProductReview_ProductReviewID] PRIMARY KEY ([ProductReviewID] ASC))GOALTER TABLE [Production].[ProductReview] WITH CHECK ADD CONSTRAINT [FK_ProductReview_Product_ProductID] FOREIGN KEY([ProductID]) REFERENCES [Production].[Product] ([ProductID])GOALTER TABLE [Production].[ProductReview] CHECK CONSTRAINT [FK_ProductReview_Product_ProductID]GOCREATE NONCLUSTERED INDEX [IX_ProductReview_ProductID_Name] ON [Production].[ProductReview] ([ProductID] ASC, [ReviewerName] ASC)INCLUDE ([Comments])GO