CREATE TABLE [Purchasing].[PurchaseOrderHeader](	  [PurchaseOrderID] INT NOT NULL IDENTITY(1         ,1)	, [RevisionNumber] TINYINT NOT NULL DEFAULT((0))	, [Status] TINYINT NOT NULL DEFAULT((1))	, [EmployeeID] INT NOT NULL	, [VendorID] INT NOT NULL	, [ShipMethodID] INT NOT NULL	, [OrderDate] DATETIME NOT NULL DEFAULT(getdate())	, [ShipDate] DATETIME NULL	, [SubTotal] MONEY NOT NULL DEFAULT((0.00))	, [TaxAmt] MONEY NOT NULL DEFAULT((0.00))	, [Freight] MONEY NOT NULL DEFAULT((0.00))	, [TotalDue] AS (isnull(([SubTotal]+[TaxAmt])+[Freight],(0)))	, [ModifiedDate] DATETIME NOT NULL DEFAULT(getdate())	, CONSTRAINT [PK_PurchaseOrderHeader_PurchaseOrderID] PRIMARY KEY ([PurchaseOrderID] ASC))GOALTER TABLE [Purchasing].[PurchaseOrderHeader] WITH CHECK ADD CONSTRAINT [FK_PurchaseOrderHeader_Vendor_VendorID] FOREIGN KEY([VendorID]) REFERENCES [Purchasing].[Vendor] ([BusinessEntityID])GOALTER TABLE [Purchasing].[PurchaseOrderHeader] CHECK CONSTRAINT [FK_PurchaseOrderHeader_Vendor_VendorID]GOALTER TABLE [Purchasing].[PurchaseOrderHeader] WITH CHECK ADD CONSTRAINT [FK_PurchaseOrderHeader_ShipMethod_ShipMethodID] FOREIGN KEY([ShipMethodID]) REFERENCES [Purchasing].[ShipMethod] ([ShipMethodID])GOALTER TABLE [Purchasing].[PurchaseOrderHeader] CHECK CONSTRAINT [FK_PurchaseOrderHeader_ShipMethod_ShipMethodID]GOALTER TABLE [Purchasing].[PurchaseOrderHeader] WITH CHECK ADD CONSTRAINT [FK_PurchaseOrderHeader_Employee_EmployeeID] FOREIGN KEY([EmployeeID]) REFERENCES [HumanResources].[Employee] ([BusinessEntityID])GOALTER TABLE [Purchasing].[PurchaseOrderHeader] CHECK CONSTRAINT [FK_PurchaseOrderHeader_Employee_EmployeeID]GOCREATE NONCLUSTERED INDEX [IX_PurchaseOrderHeader_EmployeeID] ON [Purchasing].[PurchaseOrderHeader] ([EmployeeID] ASC)GOCREATE NONCLUSTERED INDEX [IX_PurchaseOrderHeader_VendorID] ON [Purchasing].[PurchaseOrderHeader] ([VendorID] ASC)GO