CREATE TABLE [Sales].[CreditCard](	  [CreditCardID] INT NOT NULL IDENTITY(1         ,1)	, [CardType] NVARCHAR(50) COLLATE Latin1_General_CI_AS NOT NULL	, [CardNumber] NVARCHAR(25) COLLATE Latin1_General_CI_AS NOT NULL	, [ExpMonth] TINYINT NOT NULL	, [ExpYear] SMALLINT NOT NULL	, [ModifiedDate] DATETIME NOT NULL DEFAULT(getdate())	, CONSTRAINT [PK_CreditCard_CreditCardID] PRIMARY KEY ([CreditCardID] ASC))GOCREATE UNIQUE NONCLUSTERED INDEX [AK_CreditCard_CardNumber] ON [Sales].[CreditCard] ([CardNumber] ASC)GO