CREATE TABLE [Sales].[CustomerCategories](	  [CustomerCategoryID] INT NOT NULL DEFAULT(NEXT VALUE FOR [Sequences].[CustomerCategoryID])	, [CustomerCategoryName] NVARCHAR(50) COLLATE Latin1_General_100_CI_AS NOT NULL	, [LastEditedBy] INT NOT NULL	, [ValidFrom] DATETIME2(7) NOT NULL	, [ValidTo] DATETIME2(7) NOT NULL	, CONSTRAINT [PK_Sales_CustomerCategories] PRIMARY KEY ([CustomerCategoryID] ASC))GOALTER TABLE [Sales].[CustomerCategories] WITH CHECK ADD CONSTRAINT [FK_Sales_CustomerCategories_Application_People] FOREIGN KEY([LastEditedBy]) REFERENCES [Application].[People] ([PersonID])GOALTER TABLE [Sales].[CustomerCategories] CHECK CONSTRAINT [FK_Sales_CustomerCategories_Application_People]GOCREATE UNIQUE NONCLUSTERED INDEX [UQ_Sales_CustomerCategories_CustomerCategoryName] ON [Sales].[CustomerCategories] ([CustomerCategoryName] ASC)GO